import { Box, Button, GluestackUIProvider, Text } from '@gluestack-ui/themed';
import FeatureCard from "./FeatureCard";
import { ScrollView } from 'react-native';
import Gradient from '../assets/Icons/Gradient';
import { Motion } from "@legendapp/motion"
import { configureMotion } from '@legendapp/motion';
configureMotion({ timing: 's' });
import {NavigationContainer} from '@react-navigation/native';
// import HomeScreen from './HomeScreen';

import { TouchableOpacity } from 'react-native-gesture-handler';



import LightBulbPerson from '../assets/Icons/LightbulbPerson';
import Rocket from '../assets/Icons/Rocket';
import { useState } from 'react';







const Container = () => {
  
  const [screen, setScreen] = useState(<HomeScreen></HomeScreen>);
    return (
      <Box flex={1} backgroundColor="$black">
        <ScrollView
          style={{ height: '100%' }}
          contentContainerStyle={{ flexGrow: 1 }}
        >
          <Box
            position="absolute"
            $base-h={500}
            $base-w={500}
            $lg-h={700}
            $lg-w={700}
          >
            <Gradient />
          </Box>
          {screen}
          
        </ScrollView>
      </Box>
    );
  };
  export default Container;


  

const HomeScreen = () => {
  return (
      <Box
      height="60%"
      $base-my="$16"
      $base-mx="$5"
      $base-h="80%"
      $lg-my="$24"
      $lg-mx="$32"
      justifyContent="space-between"
      alignItems="center"
    >
      
      
        <Motion.View
              initial={{ opacity: 0 }}
              animate={{ opacity: 1}}
              whileHover={{ scale: 1.2 }}
              whileTap={{ y: 20 }}
              transition={{ duration: 1 }}
        >
          <Box justifyContent="center" alignItems="center">
           <Text color="$white" fontWeight="$medium" ml="$2" size="6xl">
                Welcom To DormDash
            </Text>
            </Box>
        </Motion.View>
        
        
      
      <Box $base-flexDirection="column" $md-flexDirection="row">

      <FeatureCard
          iconSvg={LightBulbPerson}
          name="I'm A Deliverer"
          desc="Make money with flexible hours!"
        />
        <TouchableOpacity 
          onPress={() => {
            console.log("hi");
          }} 
          >
          <FeatureCard
            iconSvg={Rocket}
            name="I Need Stuff Moved"
            desc="Get things delivered straight to your dorm with no hassle!" 
            
          />
        </TouchableOpacity>
        

      </Box>
      
    </Box>
  );
};


