import { config } from '@gluestack-ui/config';
import { Box, GluestackUIProvider, Text } from '@gluestack-ui/themed';
import { ScrollView } from 'react-native';
import Gradient from './assets/Icons/Gradient';
import DocumentData from './assets/Icons/DocumentData';
import LightBulbPerson from './assets/Icons/LightbulbPerson';
import Rocket from './assets/Icons/Rocket';
import Logo from './assets/Icons/Logo';
import { Motion } from "@legendapp/motion"
import { configureMotion } from '@legendapp/motion';
configureMotion({ timing: 's' });


/*
REQUIERMENTS 

drivers should be able to upload schodeual 
* shoould be stored in firebase 
* use MUI for frunt end calendar and date-time pickers 



carless should be able to day they need somthing picked up and moved 
* should be stored in firebase untill compleat 
* shoud lbe able to spesify location 


algorithom finds match

carless pays when converming match 
* strip 

drvier is payed after delivery is made 
* stripe 

refund is givenn if nothing is acutualy droped off 
* strype 


*/

export default function App() {
  return (
    <GluestackUIProvider config={config}>
      <Home />
    </GluestackUIProvider>
  );
}

const Home = () => {
  return <Container />;
};

const FeatureCard = ({ iconSvg: IconSvg, name, desc }: any) => {

  return (
    <Box
      flexDirection="column"
      borderWidth={1}
      borderColor="$borderDark700"
      $web-flex={1}
      m="$2"
      p="$4"
      rounded="$md"
    >
      <Box alignItems="center" display="flex" flexDirection="row">
        {/* <Image source={iconSvg} alt="document" width={22} height={22} /> */}
        <Text>
          <IconSvg />
        </Text>
        <Text fontSize={22} color="$white" fontWeight="500" ml="$2">
          {name}
        </Text>
      </Box>
      <Text color="$textDark400" mt="$2">
        {desc}
      </Text>
    </Box>
  );
};

const Container = () => {
  return (
    <Box flex={1} backgroundColor="$black">
      <ScrollView
        style={{ height: '100%' }}
        contentContainerStyle={{ flexGrow: 1 }}
      >
        <Box
          position="absolute"
          $base-h={500}
          $base-w={500}
          $lg-h={700}
          $lg-w={700}
        >
          <Gradient />
        </Box>
        <Box
          height="60%"
          $base-my="$16"
          $base-mx="$5"
          $base-h="80%"
          $lg-my="$24"
          $lg-mx="$32"
          justifyContent="space-between"
          alignItems="center"
        >


            <Motion.View
                  initial={{ opacity: 0 }}
                  animate={{ opacity: 1}}
                  whileHover={{ scale: 1.2 }}
                  whileTap={{ y: 20 }}
                  transition={{ duration: 1 }}
            >
              <Box justifyContent="center" alignItems="center">
               <Text color="$white" fontWeight="$medium" ml="$2" size="6xl">
                    Welcom To DormDash
                </Text>
                </Box>
            </Motion.View>



          <Box $base-flexDirection="column" $md-flexDirection="row">
          <FeatureCard
              iconSvg={Log}
              name="I'm A Deliverer"
              desc="Make money with flexible hours!"
            />

            <FeatureCard
              iconSvg={Rocket}
              name="I Need Stuff Moved"
              desc="Get things delivered straight to your dorm with no hassle!" 
            />
          </Box>
        </Box>
      </ScrollView>
    </Box>
  );
};
